package com.axis;

public class ConditionalStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a=10;
		int b=15;
		//Simple if
		if(a>b) {
			System.out.println("a is big!!");
		}
		
		//Simple if-else
		else {
			System.out.println("b is big!!");
		}
		
		//nested if
		
	}

}
